// client/src/context/PollContext.js
import { createContext, useState, useContext } from 'react';

const PollContext = createContext();

export const PollProvider = ({ children }) => {
  const [recentPolls, setRecentPolls] = useState([]);
  const [loading, setLoading] = useState(false);

  const addRecentPoll = (poll) => {
    setRecentPolls(prev => [poll, ...prev].slice(0, 5));
  };

  return (
    <PollContext.Provider value={{ recentPolls, addRecentPoll, loading, setLoading }}>
      {children}
    </PollContext.Provider>
  );
};

export const usePollContext = () => useContext(PollContext);
