// PollContext.js
const PollContext = createContext();

export const PollProvider = ({ children }) => {
  const [state, setState] = useState({ polls: [] });

  const updatePoll = updatedPoll => {
    setState(prev => ({
      polls: prev.polls.map(p => (p.id === updatedPoll.id ? updatedPoll : p))
    }));
  };

  return (
    <PollContext.Provider value={{ state, updatePoll }}>
      {children}
    </PollContext.Provider>
  );
};

export const usePollContext = () => useContext(PollContext);
