import React, { useState } from 'react';
import '../styles/CreatePoll.css';
import { createPoll } from '../services/api';

export default function CreatePoll() {
  const [question, setQuestion] = useState('');
  const [options, setOptions] = useState(['', '', '']);
  const [message, setMessage] = useState('');

  const handleOptionChange = (idx, value) => {
    const newOptions = [...options];
    newOptions[idx] = value;
    setOptions(newOptions);
  };

  const addOption = () => setOptions([...options, '']);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await createPoll({ question, options });
      setMessage(
        <span>
          Poll created! <a href={`/poll/${res.data.pollId}`}>Go to Poll</a>
        </span>
      );
    } catch (err) {
      setMessage('Error creating poll');
    }
  };

  return (
    <div className="create-poll-page">
      <div className="create-poll-container">
        <div className="form-wrapper">
          <form className="create-poll-form" onSubmit={handleSubmit}>
            <div className="form-header">
              <h2>Create Your Poll</h2>
              <p>Create and share polls in seconds</p>
            </div>
            
            <div className="form-section">
              <label>
                <span className="label-text">Poll Question</span>
                <input
                  type="text"
                  value={question}
                  onChange={e => setQuestion(e.target.value)}
                  required
                  className="poll-input"
                  placeholder="What would you like to ask?"
                />
              </label>
            </div>

            <div className="form-section">
              <label>
                <span className="label-text">Poll Options</span>
                <div className="options-list">
                  {options.map((opt, idx) => (
                    <input
                      key={idx}
                      type="text"
                      placeholder={`Option ${idx + 1}`}
                      value={opt}
                      onChange={e => handleOptionChange(idx, e.target.value)}
                      required
                      className="poll-input"
                    />
                  ))}
                </div>
              </label>
            </div>

            <div className="form-actions">
              <button type="button" onClick={addOption} className="add-btn">
                <span className="btn-icon">+</span>
                Add Option
              </button>
              <button type="submit" className="submit-btn">
                <span className="btn-icon">ðŸš€</span>
                Create Poll
              </button>
            </div>
          </form>
          
          {message && (
            <div className="message-container">
              <p className="poll-message">{message}</p>
            </div>
          )}
          
          <div className="info-note">
            <p>ðŸ’¡ After creating a poll, share the unique link with others so they can vote!</p>
          </div>
        </div>
      </div>
    </div>
  );
}
