// client/src/pages/PollResultsPage.js
import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { usePollContext } from '../context/PollContext';
import PollResults from '../components/PollResults';
import api from '../services/api'; // assuming you have this
import LoadingSpinner from '../components/LoadingSpinner';

const PollResultsPage = () => {
  const { id } = useParams();
  const { state } = usePollContext() || {}; // safe fallback
  const [poll, setPoll] = useState(null);
  const [loading, setLoading] = useState(true);

  // Try to find poll in context
  useEffect(() => {
    if (state?.polls?.length) {
      const contextPoll = state.polls.find(p => p.id === id);
      if (contextPoll) {
        setPoll(contextPoll);
        setLoading(false);
        return;
      }
    }

    // Fallback: fetch from API
    const fetchPoll = async () => {
      try {
        const data = await api.getPoll(id);
        setPoll(data);
      } catch (err) {
        console.error('Error fetching poll:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchPoll();
  }, [id, state]);

  if (loading) return <LoadingSpinner />;

  if (!poll) return <div>Poll not found</div>;

  const totalVotes = poll.options.reduce((sum, opt) => sum + opt.votes, 0);

  return (
    <div className="results-page">
      <h2>Poll Results</h2>
      <PollResults options={poll.options} totalVotes={totalVotes} />
    </div>
  );
};

export default PollResultsPage;
