import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchAllPolls } from '../services/api';
import './PreviousPolls.css';

const PreviousPolls = () => {
  const [polls, setPolls] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const getPolls = async () => {
      try {
        const res = await fetchAllPolls();
        setPolls(res.data);
      } catch (err) {
        console.error('Error fetching polls:', err.message);
      } finally {
        setLoading(false);
      }
    };
    getPolls();
  }, []);

  const handleVote = (pollId) => navigate(`/poll/${pollId}`);
  const handleResults = (pollId) => navigate(`/poll/${pollId}/results`);

  return (
    <div className="previous-polls-container">
      <h2>All Polls</h2>
      {loading ? (
        <p>Loading polls...</p>
      ) : polls.length === 0 ? (
        <p>No polls found.</p>
      ) : (
        <ul className="poll-list">
          {polls.map((poll) => (
            <li key={poll.pollId} className="poll-item">
              <div>
                <strong>{poll.question}</strong>
              </div>
              <div>
                {poll.isOpen ? (
                  <button onClick={() => handleVote(poll.pollId)}>Vote</button>
                ) : (
                  <span>Poll closed</span>
                )}
                <button onClick={() => handleResults(poll.pollId)} style={{ marginLeft: '10px' }}>
                  Results
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default PreviousPolls;
