import React, { useState } from 'react';
import Footer from '../components/Footer';
import '../styles/CreatePoll.css';
import { createPoll } from '../services/api';

export default function CreatePoll() {
  const [question, setQuestion] = useState('');
  const [options, setOptions] = useState(['', '', '']);
  const [message, setMessage] = useState('');

  const handleOptionChange = (idx, value) => {
    const newOptions = [...options];
    newOptions[idx] = value;
    setOptions(newOptions);
  };

  const addOption = () => setOptions([...options, '']);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await createPoll({ question, options });
      setMessage(
        <span>
          Poll created! <a href={`/poll/${res.data.pollId}`}>Go to Poll</a>
        </span>
      );
    } catch (err) {
      setMessage('Error creating poll');
    }
  };

  return (
    <div className="create-poll-bg">
      <div className="create-poll-container">
        <form className="create-poll-form" onSubmit={handleSubmit}>
          <h2>
            <span className="form-icon">üìù</span> Create Poll
          </h2>
          <label>
            Question:
            <input
              type="text"
              value={question}
              onChange={e => setQuestion(e.target.value)}
              required
              className="poll-input"
              placeholder="Type your poll question here..."
            />
          </label>
          <div className="options-list">
            {options.map((opt, idx) => (
              <input
                key={idx}
                type="text"
                placeholder={`Option ${idx + 1}`}
                value={opt}
                onChange={e => handleOptionChange(idx, e.target.value)}
                required
                className="poll-input"
              />
            ))}
          </div>
          <div className="poll-actions">
            <button type="button" onClick={addOption} className="add-btn">
              <span className="btn-icon">+</span> Add Option
            </button>
            <button type="submit" className="submit-btn">
              <span className="btn-icon">üöÄ</span> Create
            </button>
          </div>
        </form>
        {message && <p className="poll-message">{message}</p>}
        <p className="poll-note">
          After creating a poll, share the poll link with others so they can vote!
        </p>
      </div>
      <Footer />
    </div>
  );
}
