import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getPoll, votePoll } from '../services/api';

export default function PollPage() {
  const { id } = useParams();
  const [poll, setPoll] = useState(null);
  const [selected, setSelected] = useState(null);
  const [message, setMessage] = useState('');

  useEffect(() => {
    getPoll(id)
      .then(res => setPoll(res.data))
      .catch(() => setMessage('Poll not found'));
  }, [id]);

  const handleVote = async () => {
    try {
      await votePoll(id, { optionIndex: selected });
      setMessage('Vote submitted!');
      // Refresh poll data
      const res = await getPoll(id);
      setPoll(res.data);
    } catch (err) {
      setMessage('Error voting: ' + (err.response?.data?.error || ''));
    }
  };

  if (!poll) return <div>{message || 'Loading poll...'}</div>;

  return (
    <div>
      <h2>{poll.question}</h2>
      <ul>
        {poll.options.map((opt, idx) => (
          <li key={idx}>
            <button
              disabled={selected === idx}
              onClick={() => setSelected(idx)}
            >
              {opt.text} ({opt.votes})
            </button>
          </li>
        ))}
      </ul>
      <button onClick={handleVote} disabled={selected === null}>Vote</button>
      {message && <p>{message}</p>}
    </div>
  );
}
